
import React from 'react'
import { useEffect, useState } from "react"
import "./MainContent1.scss"

const CardSection = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    setIsVisible(true);
  }, []);

  const data = [
    {
      title: "Rau củ tươi",
      subtitle: "Được kiểm định rõ ràng",
      image: "data:image/jpeg;base64,",
    },
    {
      title: "Thực phẩm sạch",
      subtitle: "Quy trình sản xuất kín",
      image: "data:image/jpeg;base64,",
    },
    {
      title: "Trái cây tươi",
      subtitle: "Nhập khẩu & Trong nước",
      image: "data:image/jpeg;base64,",
    },
  ];

  return (
    <div className={`bg-[#fafafa] py-10 card-section ${isVisible ? 'visible' : ''}`}>
      <div className="container mx-auto px-4 grid grid-cols-1 md:grid-cols-3 gap-6">
        {data.map((item, index) => (
          <div
            key={index}
            className="card-item relative overflow-hidden rounded-xl shadow-md border-2 border-green-500"
            style={{ animationDelay: `${index * 0.2}s` }}
          >
            <div className="card-image-container">
              <img src={item.image || "/placeholder.svg"} alt={item.title} className="w-full h-[220px] object-cover" />
            </div>
            <div className="card-overlay absolute inset-0 bg-black bg-opacity-30 flex flex-col justify-center items-center text-white text-center p-4">
              <h3 className="card-title text-2xl font-bold drop-shadow-md">{item.title}</h3>
              <p className="card-subtitle text-lg drop-shadow-sm">{item.subtitle}</p>
              <div className="card-shine"></div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CardSection;
